const uint8_t irreducible_poly = 0x1b; // x^4+x^3+x+1

void InitGFNI() {
  for (int16_t y = 0; y < 256; ++y) {
    uint64_t mt = 0;
    uint8_t row = y;
    // –ù–∞ –∫–∞–∂–¥–æ–π –∏—Ç–µ—Ä–∞—Ü–∏–∏ —Ü–∏–∫–ª–∞ –≤—ã—á–∏—Å–ª—è–µ—Ç—Å—è –ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏–µ y
    // –Ω–∞ –æ—á–µ—Ä–¥–Ω–æ–π –±–∞–∑–∏—Å–Ω—ã–π –≤–µ–∫—Ç–æ—Ä, —Ä–µ–∑—É–ª—å—Ç–∞—Ç –∑–∞–ø–∏—Å—ã–≤–∞–µ—Ç—Å—è –≤ mt
    for (size_t i = 0, shift = 0; i < 8; ++i, shift += 8) {
      mt |= ((uint64_t)row << shift);
      row = (row << 1) ^ ((row >> 7) * irreducible_poly);
    }

    // –§–∞–∫—Ç–∏—á–µ—Å–∫–∏ –≤ mt —É–∂–µ –ª–µ–∂–∏—Ç –Ω—É–∂–Ω–∞—è –º–∞—Ç—Ä–∏—Ü–∞, –Ω–æ –ø–æ—Ä—è–¥–æ–∫ —ç–ª–µ–º–µ–Ω—Ç–æ–≤
    // –≤ GF2P8AFFINEQB –Ω–µ–º–Ω–æ–≥–æ —Å—Ç—Ä–∞–Ω–Ω—ã–π, –Ω—É–∂–Ω–æ –Ω–µ–º–Ω–æ–≥–æ –ø–æ–ø–µ—Ä–µ—Å—Ç–∞–≤–ª—è—Ç—å –±–∏—Ç—ã
    gfni_matrix[y] = 0;
    for (size_t i = 0; i < 8; ++i) {
      for (size_t j = 0; j < 8; ++j) {
        gfni_matrix[y] |= ((mt >> (8 * i + j)) & 1) << (8 * j + (7 - i));
      }
    }
  }
}

